        facialhair: "1",
        clothes: "1",
        headwear: "1",
        facewear: "1",
        cigarpipe: "1",
      },
      isHovered: {
        background: false,
        species: false,
        face: false,
        facialhair: false,
        clothes: false,
        headwear: false,
        facewear: false,
        cigarpipe: false
      },
      hoverArrows: false,
    }
  },
  methods: {
    toggleHoverArrows(event) {
      const childElements = event.target.querySelectorAll('.cls-1');
      const randomColor = hexaColors.hexaColors[Math.floor(Math.random() * hexaColors.hexaColors.length)];
      childElements.forEach((childElement) => {
        if (childElement.classList.contains('highlighted')) {
          childElement.style.stroke = '#41403E';
          childElement.classList.remove('highlighted');
        } else {
          childElement.style.stroke = randomColor;
          childElement.classList.add('highlighted');
 
        }
      });
    },
    prevColor(trait) {
      const currentIndex = this.currentIndex[trait];
      const traitLength = Object.keys(this.traits[trait]).length;

      if (currentIndex > 1) {
        this.currentIndex[trait] = String(parseInt(currentIndex) - 1);
      } else {
        this.currentIndex[trait] = String(traitLength);
      }

      const traitCanvas = document.getElementById(trait + 'BuilderCanvas');
      traitCanvas.style.backgroundImage = `url('../src/assets/images/traits/${trait}/${this.traits[trait][this.currentIndex[trait]]}.png')`;
      traitCanvas.style.backgroundSize = 'cover';
    },
    nextColor(trait) {
      const currentIndex = this.currentIndex[trait];
      const traitLength = Object.keys(this.traits[trait]).length;

      if (currentIndex < traitLength) {
        this.currentIndex[trait] = String(parseInt(currentIndex) + 1);
      } else {
        this.currentIndex[trait] = "1";
      }

      const traitCanvas = document.getElementById(trait + 'BuilderCanvas');
      traitCanvas.style.backgroundImage = `url('../src/assets/images/traits/${trait}/${this.traits[trait][this.currentIndex[trait]]}.png')`;
      traitCanvas.style.backgroundSize = 'cover';
    },
    removeTrait: function (trait) {
      const traitCanvas = document.getElementById(trait + 'BuilderCanvas');
      traitCanvas.style.backgroundImage = '';
      if (trait === 'background') {
        traitCanvas.style.backgroundImage = `url('../src/assets/images/traits/background/blank.jpg')`;
      } traitCanvas.style.backgroundSize = 'cover';
    }

  },
  mounted() {

  }
}

</script>


